}

groupBy = context.get("groupBy");
if (groupBy===undefined) {
    groupBy = "1m";
    context.set("groupBy",groupBy);
}

navigateBy = context.get("navigateBy")
if (navigateBy===undefined) {
    navigateBy = 2*hour;
    context.set("navigateBy",navigateBy);
}

fromDate = context.get("fromDate");
if (fromDate===undefined) {
    fromDate = new Date().getTime() - 2*hour
    context.set("fromDate",fromDate);
}

toDate = context.get("toDate");
if (toDate===undefined) {
    toDate = new Date().getTime();
    context.set("toDate",toDate);
}

switch(msg.topic){
    case "paramType":
        dataType = msg.payload;
        context.set("dataType",dataType);
    break;
    case "groupType":
        groupBy = msg.payload;
        context.set("groupBy",groupBy);
    break;
    case "navigationType":
        navigateBy = msg.payload;
        context.set("navigateBy",navigateBy);
        fromDate = Number(toDate) - Number(navigateBy);
        context.set("fromDate",fromDate);
    break;
    case "back":
        toDate = fromDate;
        context.set("toDate",toDate);
        fromDate = Number(toDate) - Number(navigateBy);
        context.set("fromDate",fromDate);
    break;
    case "next":
        fromDate = toDate;
        context.set("fromDate", fromDate);
        toDate = Number(fromDate) + Number(navigateBy);
        context.set("toDate", toDate);
    break;
    case "reset":
        var today0h = new Date().setHours(0,0,0,0);
        fromDate = today0h;
        context.set("fromDate",fromDate);
        toDate = today0h + day;
        context.set("toDate",toDate);
        msg.fromNow = fromDate;
        msg.toNow = toDate;
    break;
    case "from":
        fromDate = msg.payload;
        context.set("fromDate",fromDate);
    break;
    case "to":
        toDate = msg.payload;
        context.set("toDate",toDate);
    break;
    case "save":
        save = "save";
    break;
}

